#lines below to calculate Shannon Entropy for the lin_dict markers for the trial lineage groups
# Entropy = -[p(L1)log(p(L1)) + p(L2)log(p(L2)) + p(L3)log(p(L3))]
# where p is probability = (number with marker) / 1233 and log is to base 2.



def entropy(r_list):  #to compute Shannon Entropy for each 3-lineage group
	
	a = r_list[0]/1233
	b = r_list[1]/1233
	c = r_list[2]/1233
	if a == 0: 
		a = 1  #setting the variable to 0 in the E equation below since math.log(1,2) = 0
	if b == 0:
		b = 1
	if c == 0:
		c = 1
	E = -((a)*math.log(a,2) + (b)*math.log(b,2) + (c)*math.log(c,2))
	
	return(E)




entropy_dict = {}

for key in lin_dict:
	val = lin_dict[key]
	E = entropy(val)
	entropy_dict[key] = E
	
print('Markers (keys) and Entropy (values) = ', entropy_dict)

Total_E = 0
for key in entropy_dict:
	ent = entropy_dict[key]
	Total_E += ent
	
print('Total Entropy = ',Total_E)
	


















